document.addEventListener("DOMContentLoaded",(function(){dropdownsNames=["language","contacts"],dropdownsNames.forEach(o=>{var n=document.getElementsByClassName("js-"+o+"__dropdown")[0];n.onclick=function(){var o=n.nextElementSibling;n.classList.contains("dropdown_active")?(n.classList.remove("dropdown_active"),o.classList.remove("dropdown__container_active")):(n.classList.add("dropdown_active"),o.classList.add("dropdown__container_active"))}}),window.onclick=function(o){if(!o.target.matches(".dropdown__container, .dropdown__content"))for(var n=document.getElementsByClassName("dropdown"),t=0;t<n.length;t++){var e=n[t],s=e.nextElementSibling;if(e.classList.contains("dropdown_active")){var a=o.target.classList.value.match(/js-(\w+)__dropdown/);null==a?(e.classList.remove("dropdown_active"),s.classList.remove("dropdown__container_active")):e.classList.contains("js-"+a[1]+"__dropdown")||(e.classList.remove("dropdown_active"),s.classList.remove("dropdown__container_active"))}}}}));